import { Component } from 'react';

import logo from './logo.svg';
import './App.css';

// here we replaced the function App() {}
//                              return () 
// with the following, everything wrapped also inside the render () method.
// this is a class compenent, instead of a functional component


// this app component represents the entire application
// this is a best practice.
class App extends Component {
  constructor() {   // constructor call always run first.
    super();

      this.state = { // initialise state as empty array.
        monsters: [],
        searchField: ''
      };
      console.log("constructor");
  }

  // inside here will get run whenever the component mounts
  // mounting: the first time a component gets placed onto the DOM.
  componentDidMount() { // lifecycle method
    // fetch returns a promise, then() (asyncronous), which says "eventually I'll have a value"
    console.log("componentDidMount")
    fetch("https://jsonplaceholder.typicode.com/users")
    .then((response) => response.json()) // this is what is returned from .then, gets passed to (users)
    .then ((users) => 
      this.setState(
        () => {
        return {monsters: users}
        },
        () => {
        console.log(this.state); // log out our new state
        }
      )
    );
  }


  render() {
    console.log("render");

    const filteredMonsters = this.state.monsters.filter((monster)=> {
      return monster.name.toLocaleLowerCase().includes(this.state.searchField);
    });

    return ( 
      <div className="App">
        <input 
          className="search-box"
          type="search"
          placeholder="search monsters"
          onChange={(event) => { 
            const searchField = event.target.value.toLocaleLowerCase();
            this.setState(
              () => {
                return { searchField };
              }
            );
          }}
        />

        {filteredMonsters.map((monster) => {
            // elements returned from map() here need unique key values, which will be strings.
            // API's will all have these. like a serial number. only react sees this (not rendered)
            // in this case, these will be rendered into h1s inside divs
            return (
              <div key={monster.id}>
                <h1>{monster.name}</h1>
              </div>
            )
        })} 
      </div>
    );
  }

} 

export default App;
