// this is a class compenent, instead of a functional component


// this app component represents the entire application
// this is a best practice
class App extends Component {

  constructor() {
    // you gotta call super() when you call component
    super();
      this.state = {
        monster1: {
          name: "Linda",
          email: "linda1@gmail.com"
        },
        monster2: { 
          name: "Paul",
          email: "paul234@gmail.com"
        },
    }
  }


  render() { 
    return <div className="App"> 
        <h1>{this.state.monster1.name}</h1>
        <h1>{this.state.monster2.name}</h1>
        <h1>{this.state.monster3.name}</h1>
      </div>; 
  }

} 

export default App;
